function burgerMenu(){const e=document.querySelector(".burger"),i=document.querySelector(".menu__list"),s=document.querySelector("body");e.addEventListener("click",(()=>{i.classList.contains("active")?(i.classList.remove("active"),e.classList.remove("active-burger"),s.classList.remove("locked")):(i.classList.add("active"),e.classList.add("active-burger"),s.classList.add("locked"))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(i.classList.remove("active"),e.classList.remove("active-burger"),s.classList.remove("locked"))}))}function fixedNav(){const e=document.querySelector("nav");window.scrollY>=1?e.classList.add("fixed__nav"):e.classList.remove("fixed__nav")}burgerMenu(),window.addEventListener("scroll",fixedNav);const swiper=new Swiper(".swiper",{direction:"horizontal",loop:!0,slidesPerView:2.4,spaceBetween:50,pagination:{el:".swiper-pagination",clickable:!0},navigation:{nextEl:".swiper-button-next",prevEl:".swiper-button-prev"},breakpoints:{992:{slidesPerView:2.4},840:{slidesPerView:2},768:{slidesPerView:1.8},640:{slidesPerView:1.5},480:{slidesPerView:1.15},0:{slidesPerView:1}}});